            if (roi == null) { throw new ArgumentNullException("roi"); }
            roi.ThrowIfDisposed();
            if (options == null) { throw new ArgumentNullException("options"); }
            CVI_EdgeOptions2 cviOptions = new CVI_EdgeOptions2();
