            CVI_WaveletOptions cviWaveletOptions = new CVI_WaveletOptions();
            cviWaveletOptions.ConvertFromExternal(waveletOptions);
            CVI_CooccurrenceOptions cviCooccurrenceOptions = new CVI_CooccurrenceOptions();
